general_tools.description:
{
  "type": "object",
  "properties": {
    "customer_email": {
      "type": "string",
      "description": "Correo electrónico del cliente para enviar la cotización y reportes."
    },
    "report_summary": {
      "type": "string",
      "description": "Resumen detallado de los productos e intereses capturados durante la llamada."
    },
    "send_report_now": {
      "type": "boolean",
      "description": "Confirmación de si el cliente aceptó recibir el reporte por correo."
    },
    "call_outcome": {
      "type": "string",
      "enum": ["Venta Exitosa", "Cita Agendada", "Seguimiento Pendiente"],
      "description": "Estado final de la gestión comercial."
    }
  },
  "required": ["customer_email", "report_summary", "call_outcome"]
}

variables.description:
{
  "type": "object",
  "properties": {
    "customer_name": {
      "type": "string",
      "description": "Nombre del ganadero o cliente."
    },
    "prod_interest": {
      "type": "string",
      "description": "Equipo de Softgan solicitado (Básculas, Bretes, etc.)"
    },
    "call_outcome": {
      "type": "string",
      "description": "Resultado final: Venta, Cita o Seguimiento."
    }
  },
  "required": [
    "customer_name",
    "call_outcome"
  ]
}
